Ratings Over Time


df |&gt; 
  filter(Year_Month != "missing") |&gt; 
  mutate(Year_Month = ym(Year_Month),
         Year = year(Year_Month),
         Month = month(Year_Month)) |&gt; 
  group_by(Park) |&gt; 
  count(Year) |&gt; 
  rename(Ratings = n) |&gt; 
  ggplot(aes(Year, Ratings, color = Park)) +
  geom_line()

